import { Component, OnInit, ViewChild } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { MatPaginator } from '@angular/material/paginator';
import { MatSnackBar, MatSnackBarRef, SimpleSnackBar } from '@angular/material/snack-bar';
import { MatTableDataSource } from '@angular/material/table';
import { ConfirmComponent } from 'src/app/modules/shared/components/confirm/confirm.component';
import { CategoryService } from 'src/app/modules/shared/services/category.service';
import { NewCategoryComponent } from '../new-category/new-category.component';

@Component({
  selector: 'app-category',
  templateUrl: './category.component.html',
  styleUrls: ['./category.component.css']
})
export class CategoryComponent implements OnInit{

  constructor(private categoryService: CategoryService,
              public dialog: MatDialog, private snackBar: MatSnackBar){ }

  ngOnInit(): void {
    this.getCategories();
  }

  displayedColumns: string[] = ['id','name','descripcion','actions'];
  dataSource = new MatTableDataSource<CategoryElement>();

  @ViewChild(MatPaginator)
  paginator!: MatPaginator;

  getCategories(){

    this.categoryService.getCategories()
    .subscribe( (data:any) =>{

      console.log("respuesta categories: ", data);
      this.processCategoriesResponse(data);

    }, (error:any) => {
      console.log("error: ", error);
    })
  }

  processCategoriesResponse(resp: any){

    const dataCategory: CategoryElement[] = [];

    if( resp.metadata[0].code == "00") {

      let listCategory = resp.categoryResponse.category;

      listCategory.forEach((element: CategoryElement) => {
        dataCategory.push(element);
      });

      this.dataSource = new MatTableDataSource<CategoryElement>(dataCategory);
      this.dataSource.paginator = this.paginator;
    }

  }

  openCategoryDialog(){
    const dialogRef = this.dialog.open(NewCategoryComponent, {
      width:'500px'
    });

    dialogRef.afterClosed().subscribe((result:any) => {
      
      if(result==1){
        this.openSnackBar("Categoría Agregada","Exito");
        this.getCategories();
      }else if(result==2){
        this.openSnackBar("Se produjo un error al guardar categoría", "Error");
      }
    });
  }

  edit(id:number, name: string, descripcion: string){
    const dialogRef = this.dialog.open(NewCategoryComponent, {
      width:'500px',
      data:{id: id, name: name, descripcion: descripcion}
    });

    dialogRef.afterClosed().subscribe((result:any) => {
      
      if(result==1){
        this.openSnackBar("Categoría Actualizada","Exito");
        this.getCategories();
      }else if(result==2){
        this.openSnackBar("Se produjo un error al actualizar categoría", "Error");
      }
    });
  }

  delete(id:any){
    const dialogRef = this.dialog.open(ConfirmComponent, {
      data:{id: id}
    });

    dialogRef.afterClosed().subscribe((result:any) => {
      
      if(result==1){
        this.openSnackBar("Categoría eliminada","Exito");
        this.getCategories();
      }else if(result==2){
        this.openSnackBar("Se produjo un error al eliminar categoría", "Error");
      }
    });
  }

  buscar(termino:string){
    if(termino.length === 0){
      return this.getCategories();
    }else{
      this.categoryService.getCategorieById(termino)
          .subscribe((resp:any)=>{
            this.processCategoriesResponse(resp);
          })
    }
  }

  openSnackBar(message: string, action:string) : MatSnackBarRef<SimpleSnackBar>{
    return this.snackBar.open(message, action, {
      duration: 2000
    })
  }



}

export interface CategoryElement{
  descripcion: string;
  id: number;
  name: string;
}
<mat-card style="background: #eee6e6; color: black;">

    <h2 class="alinear">Listado de Categorías</h2>
    <mat-card-header>
        <div>
            <mat-form-field hintLabel="Busque categoria" appearance="fill" >
                <mat-label>Buscar categorías</mat-label>
                <input matInput #input (keyup)="buscar(input.value)">

            </mat-form-field>
        </div>
    </mat-card-header>
    <mat-card-header>
        <div fxFlex fxLayout="row" fxLayoutAlign="flex-end">
            <ul fxLayout="row" fxLayoutGap="20px">
                <li>
                    <button mat-raised-button style="background: blue;" (click)="openCategoryDialog()">Agregar Categoria</button>
                </li>
            </ul>

        </div>
    </mat-card-header>

    <div mat-card-image></div>

    <mat-card-content style="background: #eee6e6; color: black;">
        <table mat-table [dataSource]="dataSource" class="mat-cell" style="background: #eee6e6; color: black;">
            
            <!-- id column-->
            <ng-container matColumnDef="id">
                <th mat-header-cell *matHeaderCellDef> Nº</th>
                <td mat-cell *matCellDef="let element"> {{ element.id}}</td>
            </ng-container>
          

            <!-- name column -->
            <ng-container matColumnDef="name">
                <th mat-header-cell *matHeaderCellDef> Nombre</th>
                <td mat-cell *matCellDef="let element"> {{ element.name}}</td>
            </ng-container>

            <!-- description column -->
            <ng-container matColumnDef="descripcion">
                <th mat-header-cell *matHeaderCellDef> Descripción </th>
                <td mat-cell *matCellDef="let element"> {{ element.descripcion}}</td>
            </ng-container>

            <!-- actions-->
            <ng-container matColumnDef="actions">
                <th mat-header-cell *matHeaderCellDef> Acciones </th>
                <td mat-cell *matCellDef="let element; let i=index;">

                    <button mat-icon-button color="accent"(click)="edit(element.id, element.name, element.descripcion)">
                        <mat-icon aria-label="Edit">edit</mat-icon>
                    </button>

                    <button mat-icon-button color="accent" (click)="delete(element.id)">
                        <mat-icon aria-label="Delete">delete</mat-icon>
                    </button>
                </td>

            </ng-container>

            <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
            <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>

        </table>

        <mat-paginator [pageSizeOptions]="[5, 10, 20]" showFirstLastButtons></mat-paginator>

    </mat-card-content>

</mat-card>
