import { Component, Inject, OnInit } from '@angular/core';
import { MatDialogRef, MAT_DIALOG_DATA } from '@angular/material/dialog';
import { CategoryService } from '../../services/category.service';
import { CategoryElement } from 'src/app/modules/category/components/category/category.component';

@Component({
  selector: 'app-detailsfiles',
  templateUrl: './detailsfiles.component.html',
  styleUrls: ['./detailsfiles.component.css']
})
export class DetailFilesComponent implements OnInit{

  constructor(public dialogRef: MatDialogRef<DetailFilesComponent>,
            @Inject (MAT_DIALOG_DATA) public data:any,
            private categoryService: CategoryService){
              console.log(data);
  }

  

  ngOnInit(): void {
    if(this.data != null){
      this.categoryService.getFilesById(this.data.id)
            .subscribe((data:any) => {
              console.log("respuesta archivo: ", data);
              this.processFilesResponse(data);
              this.dialogRef.close(1);
            }, (error:any) => {
            })
    }else{
      this.dialogRef.close(2);
    }
  }

  onNoClick(){
    this.dialogRef.close(3);
  }

  processFilesResponse(resp: any){
    const dataCategory: CategoryElement[] = [];
      let listCategory = resp.body;
      listCategory.forEach((element: CategoryElement) => {
        dataCategory.push(element);
      });
  }
}
<h1 mat-dialog-title>Details </h1>
<mat-card-content>
    <div class="row g-6 align-items-center" style="margin-top: 2%;">
      <div class="col-2">
        <label for="input" class="col-form-label">Name:</label>
      </div>
      <div class="col-6">
        <label for="input" class="col-form-label" *matCellDef="let element"><strong>{{element.nombreArchivo}}</strong></label>
      </div>
    </div>
    <div class="row g-6 align-items-center" style="margin-top: 2%;">
      <div class="col-2">
        <label for="input" class="col-form-label">Description:</label>
      </div>
      <div class="col-9">
        <label for="input" class="col-form-label" *matCellDef="let element"><strong>{{element.descripcionArchivo}}</strong></label>
      </div>
    </div>
    <div class="row g-6 align-items-center" style="margin-top: 2%;">
      <div class="col-2">
        <label for="input" class="col-form-label">Id:</label>
      </div>
      <div class="col-9">
        <label for="input" class="col-form-label" *matCellDef="let element"><strong>{{element.id_Archivo}}</strong></label>
      </div>
    </div>
    <div class="row g-6 align-items-center" style="margin-top: 2%;">
      <div class="col-2">
        <label for="input" class="col-form-label">Created At:</label>
      </div>
      <div class="col-9">
        <label for="input" class="col-form-label" *matCellDef="let element"><strong>{{element.fecha_creacion}}</strong></label>
      </div>
    </div>
    <div class="row g-6 align-items-center" style="margin-top: 2%;">
        <div class="col-2">
          <label for="input" class="col-form-label">Created By:</label>
        </div>
        <div class="col-9">
          <label for="input" class="col-form-label" *matCellDef="let element"><strong>{{element.creador}}</strong></label>
        </div>
    </div>
    <div class="row g-6 align-items-center" style="margin-top: 2%;">
        <div class="col-2">
          <label for="input" class="col-form-label">Updated At:</label>
        </div>
        <div class="col-9">
          <label for="input" class="col-form-label" *matCellDef="let element"><strong>{{element.fecha_actualizacion}}</strong></label>
        </div>
    </div>
    <div class="row g-6 align-items-center" style="margin-top: 2%;">
        <div class="col-2">
          <label for="input" class="col-form-label">Version:</label>
        </div>
        <div class="col-9">
          <label for="input" class="col-form-label" *matCellDef="let element"><strong>{{element.version}}</strong></label>
        </div>
    </div>
    <div class="row g-6 align-items-center" style="margin-top: 2%;">
        <div class="col-2">
          <label for="input" class="col-form-label">State:</label>
        </div>
        <div class="col-9">
          <label for="input" class="col-form-label" *matCellDef="let element"><strong>{{element.estado}}</strong></label>
        </div>
    </div>
  </mat-card-content>
