import { Component, Inject, OnInit } from '@angular/core';
import { MatDialogRef, MAT_DIALOG_DATA } from '@angular/material/dialog';
import { CategoryService } from '../../services/category.service';

@Component({
  selector: 'app-confirm',
  templateUrl: './confirm.component.html',
  styleUrls: ['./confirm.component.css']
})
export class ConfirmComponent implements OnInit{

  constructor(public dialogRef: MatDialogRef<ConfirmComponent>,
            @Inject (MAT_DIALOG_DATA) public data:any,
            private categoryService: CategoryService){

  }

  ngOnInit(): void {
      
  }

  onNoClick(){
    this.dialogRef.close(3);
  }

  delete(){
    if(this.data != null){
      this.categoryService.deleteCategorie(this.data.id)
            .subscribe((data:any) => {
              this.dialogRef.close(1);
            }, (error:any) => {
              this.dialogRef.close(2);
            })
    }else{
      this.dialogRef.close(2);
    }
  }
}
<h1 mat-dialog-title style="background-color: #2c3e50; color: white; margin: 0; padding: 16px; border-radius: 8px 8px 0 0;">
    Delete Twin File
  </h1>
  
  <div mat-dialog-content style="background-color: #f4f6f9; color: #2c3e50; padding: 20px;">
    <p>¿Estás seguro de eliminar el registro?</p>
  </div>
  
  <div mat-dialog-actions style="background-color: #f4f6f9; padding: 16px; display: flex; justify-content: flex-end; border-radius: 0 0 8px 8px;">
    <button mat-button (click)="onNoClick()" style="background-color: #e74c3c; color: white; border-radius: 5px; margin-right: 10px;">
      No
    </button>
    <button mat-button (click)="delete()" cdkFocusInitial style="background-color: #3498db; color: white; border-radius: 5px;">
      Sí
    </button>
  </div>
