import { Component, OnInit, ViewChild } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { MatPaginator } from '@angular/material/paginator';
import { MatSnackBar, MatSnackBarRef, SimpleSnackBar } from '@angular/material/snack-bar';
import { MatTableDataSource } from '@angular/material/table';
import { ConfirmComponent } from 'src/app/modules/shared/components/confirm/confirm.component';
import { CategoryService } from 'src/app/modules/shared/services/category.service';
import { NewCategoryComponent } from '../new-category/new-category.component';
import { DetailFilesComponent } from 'src/app/modules/shared/components/detailsfiles/detailsfiles.component';
import { ExecutefileComponent } from 'src/app/modules/shared/components/executefile/executefile.component';
import { TestfilesComponent } from 'src/app/modules/shared/components/testfiles/testfiles.component';
import { NewfilesComponent } from '../newfiles/newfiles.component';

@Component({
  selector: 'app-category',
  templateUrl: './category.component.html',
  styleUrls: ['./category.component.css']
})
export class CategoryComponent implements OnInit{

  constructor(private categoryService: CategoryService,
              public dialog: MatDialog, private snackBar: MatSnackBar){ }

  ngOnInit(): void {
    this.getCategories();
  }

  ngAfterViewInit() {
    this.dataSource.paginator = this.paginator;
  }

  displayedColumns: string[] = ['nombreArchivo','descripcionArchivo','id_Archivo','fecha_creacion','creador','fecha_actualizacion'
    ,'version','estado','actions'];
  dataSource = new MatTableDataSource<CategoryElement>();

  @ViewChild(MatPaginator) paginator!: MatPaginator;

  getCategories(){

    this.categoryService.getCategories()
    .subscribe( (data:any) =>{

      console.log("respuesta archivos: ", data);
      this.processCategoriesResponse(data);

    }, (error:any) => {
      console.log("error: ", error);
    })
  }

  processCategoriesResponse(resp: any){
    const dataCategory: CategoryElement[] = [];
      let listCategory = resp.body;
      listCategory.forEach((element: CategoryElement) => {
        dataCategory.push(element);
      });
      this.dataSource = new MatTableDataSource<CategoryElement>(dataCategory);
      this.dataSource.paginator = this.paginator;
  }

  openCategoryDialog(){
    const dialogRef = this.dialog.open(NewfilesComponent, {
      width:'500px'
    });

    dialogRef.afterClosed().subscribe((result:any) => {
      
      if(result==1){
        this.openSnackBar("Archivo Agregado","Exito");
        this.getCategories();
      }else if(result==2){
        this.openSnackBar("Se produjo un error al guardar el archivo", "Error");
      }
    });
  }

  edit(id:number, name: string, descripcion: string, creador: string, fecha_actualizacion: Date, version: number ){
    const dialogRef = this.dialog.open(NewCategoryComponent, {
      width:'700px',
      data:{id: id, name: name, descripcion: descripcion, creador, fecha_actualizacion, version}
    });

    dialogRef.afterClosed().subscribe((result:any) => {
      
      if(result==1){
        this.openSnackBar("Archivo Actualizado","Exito");
        this.getCategories();
      }else if(result==2){
        this.openSnackBar("Se produjo un error al actualizar el archivo", "Error");
      }
    });
  }

  detail(id:number){
    const dialogRef = this.dialog.open(DetailFilesComponent, {
      data:{id: id}
    });

    dialogRef.afterClosed().subscribe((result:any) => {
      if(result==1){
        this.getCategories();
      }else if(result==2){
        this.openSnackBar("Se produjo un error al ver el archivo", "Error");
      }
      
    });

  }

  doSomething($event: any) {
    $event.stopPropagation();
  }

  filtrarNombre(event: Event) {
    const filtro = (event.target as HTMLInputElement).value.trim().toLowerCase();
    const regex = new RegExp(`^${filtro}`);
    this.dataSource.filterPredicate = (data, filter) => {
      const codigo = data.nombreArchivo.toString();
      return regex.test(codigo);
    };
    this.dataSource.filter = filtro.charAt(0);
  }

  delete(id:any){
    const dialogRef = this.dialog.open(ConfirmComponent, {
      data:{id: id}
      
    });


    dialogRef.afterClosed().subscribe((result:any) => {
      
      if(result==1){
        this.openSnackBar("Categoría eliminada","Exito");
        this.getCategories();
      }else if(result==2){
        this.openSnackBar("Se produjo un error al eliminar categoría", "Error");
      }
    });
  }

  execute(id:any){
    const dialogRef = this.dialog.open(ExecutefileComponent, {
      data:{id: id}
      
    });


    dialogRef.afterClosed().subscribe((result:any) => {
      
      if(result==1){
        this.openSnackBar("Archivo ejecutado","Exito");
        this.getCategories();
      }else if(result==2){
        this.openSnackBar("Se produjo un error al ejecutar el archivo", "Error");
      }
    });
  }

  test(id:any){
    const dialogRef = this.dialog.open(TestfilesComponent, {
      data:{id: id}
      
    });


    dialogRef.afterClosed().subscribe((result:any) => {
      
      if(result==1){
        this.openSnackBar("Archivo testeado","Exito");
        this.getCategories();
      }else if(result==2){
        this.openSnackBar("Se produjo un error al testear el archivo", "Error");
      }
    });
  }

  buscar(termino:string){
    if(termino.length === 0){
      return this.getCategories();
    }else{
      this.categoryService.getFilesById(termino)
          .subscribe((resp:any)=>{
            this.processCategoriesResponse(resp);
          })
    }
  }

  openSnackBar(message: string, action:string) : MatSnackBarRef<SimpleSnackBar>{
    return this.snackBar.open(message, action, {
      duration: 2000
    })
  }



}

export interface CategoryElement{
  nombreArchivo: string;
  descripcionArchivo: string;
  id_Archivo: number;
  fecha_creacion: Date;
  creador: string;
  fecha_actualizacion: Date;
  version: string;
  estado: string;
}
<mat-card style="background: #ffffff; color: black;">

    <h1 style="color: rgb(0, 55, 111); margin-left: 2%; margin-top: 2%;" class="alinear"><strong>Definiciones</strong></h1>
    

    <div mat-card-image></div>
    <mat-card-content style="background: #ffffff; color: black;">
        <div style="max-height: 400px; overflow-y: auto;">
        <mat-table [dataSource]="dataSource"  class="example-container mat-elevation-z8 mat-cell" matSort matSortStart="desc">

            <ng-container matColumnDef="id_Archivo">
              <mat-header-cell *matHeaderCellDef mat-sort-header style="background-color: #003366; color: white; text-align: left;">
                ID
                <button mat-icon-button [matMenuTriggerFor]="aboveMenu" (click)="$event.stopPropagation()">
                 <mat-icon>filter_alt</mat-icon>
               </button>
               <mat-menu #aboveMenu="matMenu" yPosition="above" [hasBackdrop]="false" >
                <div class="contenedor-flexbox" style="display:inline-block;" mat-menu-item type="submit">
                    <mat-icon>close</mat-icon>
                </div>
                <p mat-menu-item (click)="doSomething($event);">
                    <mat-icon>search</mat-icon>
                    <input matInput (keyup)="filtrarNombre($event)" placeholder="buscar" style="width: 100px;">
                </p>
               </mat-menu>
             </mat-header-cell>
              <mat-cell *matCellDef="let element" style="background-color: #bbb8b8; color: black; border-bottom: 1px solid #dddddd;"> {{element.id_Archivo}} </mat-cell>
            </ng-container>
          
            <ng-container matColumnDef="nombreArchivo">
              <mat-header-cell *matHeaderCellDef mat-sort-header style="background-color: #003366; color: white; text-align: left;"> NOMBRE
                <button mat-icon-button [matMenuTriggerFor]="aboveMenu" (click)="$event.stopPropagation()">
                  <mat-icon>filter_alt</mat-icon>
                </button>
                <mat-menu #aboveMenu="matMenu" yPosition="above" [hasBackdrop]="false" >
                  <div class="contenedor-flexbox" style="display:inline-block;" mat-menu-item type="submit">
                      <mat-icon>close</mat-icon>
                  </div>
                  <p mat-menu-item (click)="doSomething($event);">
                    <mat-icon>search</mat-icon>
                    <input matInput (keyup)="filtrarNombre($event)" placeholder="buscar" style="width: 100px;">
                  </p>
                 </mat-menu>
              </mat-header-cell>
              <mat-cell *matCellDef="let element" style="background-color: #bbb8b8; color: black; border-bottom: 1px solid #dddddd;"> {{element.nombreArchivo}} </mat-cell>
            </ng-container>
          
            <ng-container matColumnDef="descripcionArchivo">
              <mat-header-cell *matHeaderCellDef mat-sort-header style="background-color: #003366; color: white; text-align: left;">DESCRIPCIÓN</mat-header-cell>
              <mat-cell *matCellDef="let element" style="background-color: #bbb8b8; color: black; border-bottom: 1px solid #dddddd;"> {{element.descripcionArchivo}} </mat-cell>
            </ng-container>

            <ng-container matColumnDef="creador">
                <mat-header-cell *matHeaderCellDef mat-sort-header style="background-color: #003366; color: white; text-align: left;">CREADOR</mat-header-cell>
                <mat-cell *matCellDef="let element" style="background-color: #bbb8b8; color: black; border-bottom: 1px solid #dddddd;"> {{element.creador}} </mat-cell>
            </ng-container>
          
            <ng-container matColumnDef="fecha_creacion">
              <mat-header-cell *matHeaderCellDef style="background-color: #003366; color: white; text-align: left;"> FECHA_CREACIÓN
              </mat-header-cell>
              <mat-cell *matCellDef="let element" style="background-color: #bbb8b8; color: black; border-bottom: 1px solid #dddddd;"> {{element.fecha_creacion}} </mat-cell>
            </ng-container>
            
            <ng-container matColumnDef="fecha_actualizacion">
                <mat-header-cell *matHeaderCellDef style="background-color: #003366; color: white; text-align: left;"> FECHA_ACTUALIZADA
                </mat-header-cell>
                <mat-cell *matCellDef="let element" style="background-color: #bbb8b8; color: black; border-bottom: 1px solid #dddddd;"> {{element.fecha_actualizacion}} </mat-cell>
              </ng-container>
          
            <ng-container matColumnDef="version">
              <mat-header-cell *matHeaderCellDef style="background-color: #003366; color: white; text-align: left;"> VERSIÓN</mat-header-cell>
              <mat-cell *matCellDef="let element" style="background-color: #bbb8b8; color: black; border-bottom: 1px solid #dddddd;"> {{element.version}} </mat-cell>
            </ng-container>
          
            <ng-container matColumnDef="estado" >
              <mat-header-cell *matHeaderCellDef mat-sort-header style="background-color: #003366; color: white; text-align: left;"> ESTADO </mat-header-cell>
              <mat-cell *matCellDef="let element" style="background-color: #bbb8b8; color: black; border-bottom: 1px solid #dddddd;"> {{element.estado}} </mat-cell>
            </ng-container>
          
            <ng-container matColumnDef="actions">
                <mat-header-cell *matHeaderCellDef style="background-color: #003366; color: white; text-align: left;"> ACCIONES </mat-header-cell>
                <mat-cell *matCellDef="let element; let i=index;" style="background-color: #bbb8b8; color: black; border-bottom: 1px solid #dddddd;">
                    <button mat-icon-button style="background-color: #f5f5f5; border: 1px solid #f5f5f5;" (click)="detail(element.id_Archivo)">
                        <mat-icon aria-label="Detail">manage_search</mat-icon>
                    </button>
                    <button mat-icon-button style="background-color: #f5f5f5; border: 1px solid #f5f5f5;" (click)="edit(element.id_Archivo, element.nombreArchivo, element.descripcionArchivo, element.creador, element.fecha_actualizacion, element.version)">
                        <mat-icon aria-label="Edit">edit</mat-icon>
                    </button>
                    <button mat-icon-button style="background-color: #f5f5f5; border: 1px solid #f5f5f5;" (click)="delete(element.id_Archivo)">
                        <mat-icon aria-label="Delete">delete</mat-icon>
                    </button>
                    <button mat-icon-button style="background-color: #f5f5f5; border: 1px solid #f5f5f5;" (click)="execute(element.id_Archivo)">
                        <mat-icon label="Execute">swipe_left</mat-icon>
                    </button>
                    <button mat-icon-button style="background-color: #f5f5f5; border: 1px solid #f5f5f5;" (click)="test(element.id_Archivo)">
                        <mat-icon label="Test">inventory</mat-icon>
                    </button>
                </mat-cell>
            </ng-container>
            <!-- Encabezado de la tabla -->
            <mat-header-row *matHeaderRowDef="displayedColumns; sticky: true"></mat-header-row>
          
            <!-- Filas de la tabla -->
            <mat-row *matRowDef="let cuenta; columns: displayedColumns;"></mat-row>
          
          </mat-table>
        </div>
          <mat-paginator [pageSizeOptions]="[5, 10, 20]" showFirstLastButtons class="custom-paginator"></mat-paginator>

    </mat-card-content>
    <mat-card-header>
        <div class="icon-container">
          <div class="icon-grid">
            <div class="icon-item">
              <mat-icon>search</mat-icon> Detalles
            </div>
            <div class="icon-item">
              <mat-icon>edit</mat-icon> Modificar
            </div>
            <div class="icon-item">
              <mat-icon>delete</mat-icon> Eliminar
            </div>
            <div class="icon-item">
              <mat-icon>play_circle_filled</mat-icon> Ejecutar
            </div>
            <div class="icon-item">
              <mat-icon>folder</mat-icon> Prueba
            </div>
            <div class="icon-item">
              <mat-icon>share</mat-icon> Compartir
            </div>
          </div>
        </div>
      </mat-card-header>

</mat-card>
