import { Component, Inject, OnInit } from '@angular/core';
import { MatDialogRef, MAT_DIALOG_DATA } from '@angular/material/dialog';
import { CategoryService } from '../../services/category.service';
import { CategoryElement } from 'src/app/modules/category/components/category/category.component';

@Component({
  selector: 'app-detailsfiles',
  templateUrl: './detailsfiles.component.html',
  styleUrls: ['./detailsfiles.component.css']
})
export class DetailFilesComponent implements OnInit{
  public archivo: any;
  constructor(public dialogRef: MatDialogRef<DetailFilesComponent>,
            @Inject (MAT_DIALOG_DATA) public data:any,
            private categoryService: CategoryService){
              console.log(data);
  }

  

  ngOnInit(): void {
    if(this.data != null){
      this.categoryService.getFilesById(this.data.id)
            .subscribe((data:any) => {
              console.log("respuesta archivo: ", data);
              this.archivo = data.body[0];
              console.log(this.archivo);
            }, (error:any) => {
              console.error(error);
            })
    }else{
      this.dialogRef.close(2);
    }
  }

  onNoClick(){
    this.dialogRef.close(3);
  }
}
<h1 mat-dialog-title>Details </h1>
<mat-card-content *ngIf="archivo" style="color: white;">
    <div class="row g-6 align-items-center" style="margin-top: 9%;">
      <div class="col-9">
        <label for="input" class="col-form-label"><strong>Name: </strong></label>
        <label for="input" class="col-form-label">{{archivo.nombreArchivo}}</label>
      </div>
    </div>
    <div class="row g-6 align-items-center" style="margin-top: 9%;">
      <div class="col-9">
        <label for="input" class="col-form-label"><strong>Description: </strong> </label>
        <label for="input" class="col-form-label">{{archivo.descripcionArchivo}}</label>
      </div>
    </div>
    <div class="row g-6 align-items-center" style="margin-top: 9%;">
      <div class="col-9">
        <label for="input" class="col-form-label"><strong>Id: </strong></label>
        <label for="input" class="col-form-label">{{archivo.id_Archivo}}</label>
      </div>
    </div>
    <div class="row g-6 align-items-center" style="margin-top: 9%;">
      <div class="col-9">
        <label for="input" class="col-form-label"><strong>Created At: </strong></label>
        <label for="input" class="col-form-label">{{archivo.fecha_creacion}}</label>
      </div>
    </div>
    <div class="row g-6 align-items-center" style="margin-top: 9%;">
        <div class="col-9">
          <label for="input" class="col-form-label"><strong>Created By: </strong></label>
          <label for="input" class="col-form-label">{{archivo.creador}}</label>
        </div>
    </div>
    <div class="row g-6 align-items-center" style="margin-top: 9%;">
        <div class="col-9">
          <label for="input" class="col-form-label"><strong>Updated At: </strong></label>
          <label for="input" class="col-form-label">{{archivo.fecha_actualizacion}}</label>
        </div>
    </div>
    <div class="row g-6 align-items-center" style="margin-top: 9%;">
        <div class="col-9">
          <label for="input" class="col-form-label"><strong>Version: </strong></label>
          <label for="input" class="col-form-label">{{archivo.version}}</label>
        </div>
    </div>
    <div class="row g-6 align-items-center" style="margin-top: 9%;">
        <div class="col-9">
          <label for="input" class="col-form-label"><strong>State: </strong></label>
          <label for="input" class="col-form-label">{{archivo.estado}}</label>
        </div>
    </div>
  </mat-card-content>
